// Validate Input - Node 2
const input = $input.first().json;
const errors = [];
const validations = {};

// Required fields
const required = ['templated_id', 'name', 'canvas_width', 'canvas_height'];
for (const field of required) {
  if (!input[field]) {
    errors.push(`${field}: MISSING`);
    validations[field] = 'MISSING';
  } else {
    validations[field] = 'OK';
  }
}

// Numeric validation for canvas dimensions
if (input.canvas_width !== undefined) {
  if (typeof input.canvas_width !== 'number' || input.canvas_width <= 0) {
    errors.push('canvas_width must be a positive number');
    validations.canvas_width = 'INVALID';
  }
}
if (input.canvas_height !== undefined) {
  if (typeof input.canvas_height !== 'number' || input.canvas_height <= 0) {
    errors.push('canvas_height must be a positive number');
    validations.canvas_height = 'INVALID';
  }
}

if (errors.length > 0) {
  return [{
    json: {
      success: false,
      error: 'VALIDATION_ERROR',
      message: errors[0],
      details: validations,
      _isError: true
    }
  }];
}

// Pass through validated data with defaults
return [{
  json: {
    templated_id: input.templated_id,
    name: input.name,
    canvas_width: input.canvas_width,
    canvas_height: input.canvas_height,
    preview_url: input.preview_url || null,
    is_active: input.is_active !== false,
    supports: input.supports || ['single'],
    _isError: false
  }
}];

// Transform Data - Updated for Dynamic Slots
const webhookData = $('Validate Input').first().json;
const layers = $input.all().map(item => item.json);

// NEW: Identify slot layers by naming convention
// Any layer with 'slot' in the name is replaceable
const slotLayers = layers.filter(l => 
  l.layer.toLowerCase().includes('slot') && l.type === 'image'
);

// Validate at least one slot exists
if (slotLayers.length === 0) {
  const foundLayers = layers.map(l => `${l.layer} (${l.type})`);

  return [{
    json: {
      success: false,
      error: 'LAYER_ERROR',
      message: 'Template must have at least one slot layer (layer name must contain "slot", type: image)',
      found_layers: foundLayers,
      hint: 'Rename your image layers to include "slot" (e.g., slot-before, slot-after, slot-hero)',
      _isError: true
    }
  }];
}

const { canvas_width, canvas_height } = webhookData;

// LEGACY: Find before/after layers for backwards compatibility
// Support both old naming (image-before) and new naming (slot-before)
const beforeLayer = layers.find(l => 
  (l.layer === 'image-before' || l.layer.toLowerCase().includes('slot') && l.layer.toLowerCase().includes('before')) 
  && l.type === 'image'
);
const afterLayer = layers.find(l => 
  (l.layer === 'image-after' || l.layer.toLowerCase().includes('slot') && l.layer.toLowerCase().includes('after')) 
  && l.type === 'image'
);

// Build frame preview render payload (hide all slot layers)
const framePreviewLayers = {};
slotLayers.forEach(slot => {
  framePreviewLayers[slot.layer] = { hide: true };
});

// Build Supabase record
const templateRecord = {
  templated_id: webhookData.templated_id,
  name: webhookData.name,
  thumbnail: webhookData.preview_url,
  templated_preview_url: webhookData.preview_url,
  canvas_width: canvas_width,
  canvas_height: canvas_height,

  // LEGACY: Before slot (for backwards compatibility)
  before_slot_width: beforeLayer?.width || slotLayers[0]?.width || 400,
  before_slot_height: beforeLayer?.height || slotLayers[0]?.height || 600,
  before_slot_x_percent: beforeLayer ? (beforeLayer.x / canvas_width) * 100 : 5,
  before_slot_y_percent: beforeLayer ? (beforeLayer.y / canvas_height) * 100 : 20,
  before_placeholder_url: beforeLayer?.image_url || null,

  // LEGACY: After slot (for backwards compatibility)
  after_slot_width: afterLayer?.width || slotLayers[1]?.width || slotLayers[0]?.width || 400,
  after_slot_height: afterLayer?.height || slotLayers[1]?.height || slotLayers[0]?.height || 600,
  after_slot_x_percent: afterLayer ? (afterLayer.x / canvas_width) * 100 : 52,
  after_slot_y_percent: afterLayer ? (afterLayer.y / canvas_height) * 100 : 20,
  after_placeholder_url: afterLayer?.image_url || null,

  // Metadata
  supports: webhookData.supports,
  is_active: webhookData.is_active,

  // NEW: Full layers JSON (source of truth for dynamic slots)
  layers_json: layers,

  // Slot information for frame preview generation
  _slotLayers: slotLayers.map(s => s.layer),
  _framePreviewLayers: framePreviewLayers,

  _isError: false
};

return [{ json: templateRecord }];

// Merge frame preview URL into template record
const templateRecord = $('Transform Data').first().json;
const renderResult = $input.first().json;

// Add frame preview URL from render result
templateRecord.frame_preview_url = renderResult.render_url || null;

// Remove internal fields before saving
delete templateRecord._slotLayers;
delete templateRecord._framePreviewLayers;
delete templateRecord._isError;

return [{ json: templateRecord }];
